mapper.insert:
INSERT INTO file_attachment (
    biz_type, biz_id, object_key, file_name, file_type, file_size, extra, is_deleted
) VALUES (
    #{bizType}, #{bizId}, #{objectKey}, #{fileName}, #{fileType}, #{fileSize}, #{extra}, #{deleted, jdbcType=TINYINT}
)

mapper.select:
SELECT

FROM file_attachment
WHERE is_deleted = 0
  AND biz_type = #{bizType}
  AND biz_id = #{bizId}
ORDER BY created_at DESC, id DESC
