mapper.select:
SELECT COALESCE(SUM(price + COALESCE(shipping_cost,0)), 0)
FROM purchase
WHERE asset_id = #{assetId}

mapper.insert:
INSERT INTO purchase (
    asset_id, type, name, platform_id, platform_name, seller, price, currency, quantity,
    shipping_cost, purchase_date, invoice_no, warranty_months,
    warranty_expire_date, product_link, attachments, notes
) VALUES (
    #{assetId}, #{type}, #{name}, #{platformId}, #{platformName}, #{seller}, #{price}, #{currency}, #{quantity},
    #{shippingCost}, #{purchaseDate}, #{invoiceNo}, #{warrantyMonths},
    #{warrantyExpireDate}, #{productLink}, #{attachments}, #{notes}
)
