mapper.select:
SELECT * FROM wishlist
status = #{status}
ORDER BY updated_at DESC

mapper.insert:
INSERT INTO wishlist (
    name, category_id, brand_id, model, expected_price,
    image_url, link, status, notes, priority, converted_asset_id
) VALUES (
    #{name}, #{categoryId}, #{brandId}, #{model}, #{expectedPrice},
    #{imageUrl}, #{link}, #{status}, #{notes}, #{priority}, #{convertedAssetId}
)

mapper.select:
SELECT COUNT(1)
FROM wishlist
WHERE brand_id = #{brandId}
